mapper.select:
SELECT c.id, c.sku, c.commodity_name, c.commodity_name_en, concat(c.spec_name, " ", c.spec_name_en) as spec_name, concat(cb.brand_name," ", cb.brand_name_en) as brand_name, c.img_url, c.customer_id, c.sale_status, c.review_status, c.is_correspondence, getCategoryChildId( c.category1_id, c.category2_id, c.category3_id, c.category4_id, c.category5_id ) as category_id FROM commodity c left join commodity_brand cb on c.brand_id = cb.id where c.sale_status = #{kd.saleStatus}
and c.customer_id = #{kd.customerId}
and c.brand_id = #{kd.brandId}
and (c.category1_id = #{kd.categoryId} or c.category2_id = #{kd.categoryId} or c.category3_id = #{kd.categoryId} or c.category4_id = #{kd.categoryId} or c.category5_id = #{kd.categoryId})
and c.is_correspondence = #{kd.isCorrespondence}
and c.review_status = #{kd.reviewStatus}
and (locate(#{kd.keyword}, c.sku)>0 or locate(#{kd.keyword}, c.commodity_name)>0 or locate(#{kd.keyword}, c.commodity_name_en)>0 or locate(#{kd.keyword}, c.hs_code)>0)

SELECT c.sku, c.commodity_name, c.commodity_name_en, c.customer_id, c.remark, cb.brand_name, cb.brand_name_en, getCategoryName (getCategoryChildId ( c.category1_id, c.category2_id, c.category3_id, c.category4_id, c.category5_id )) categoryName, getCategoryNameEn (getCategoryChildId ( c.category1_id, c.category2_id, c.category3_id, c.category4_id, c.category5_id )) categoryNameEn FROM commodity c left join commodity_brand cb on c.brand_id = cb.id
c.customer_id = #{customerId}
#{id}
