mapper.select:
select c.id as commodity_id, c.sku, c.commodity_name, c.commodity_name_en, c.spec_name, c.is_correspondence, c.img_url, getCategoryName (getCategoryChildId ( c.category1_id, c.category2_id, c.category3_id, c.category4_id, c.category5_id )) categoryName, getCategoryNameEn (getCategoryChildId ( c.category1_id, c.category2_id, c.category3_id, c.category4_id, c.category5_id )) categoryNameEn, s.id as id, s.available_stock_count, s.wh_id from stocks s left join commodity c on s.commodity_id = c.id where c.customer_id = #{customerId} and s.wh_id = #{whId}
and c.is_correspondence = #{isCorrespondence}
and (c.sku like concat("%" , #{keyword} , "%") or c.commodity_name like concat("%" , #{keyword} , "%") or c.commodity_name_en like concat("%" , #{keyword} , "%"))

mapper.insert:
INSERT INTO stocks ( wh_id, commodity_id, available_stock_count )
VALUE
    ( #{stockUpdBo.whId} ,
    #{stockUpdBo.commodityId} ,
    #{stockUpdBo.qty}
    )
